package controller;

import models.Customer;

import javax.servlet.*;
import javax.servlet.http.*;
import javax.servlet.annotation.*;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

@WebServlet(name = "controller.CustomerController", urlPatterns ={ "/controller.customer","/"})
public class CustomerController extends HttpServlet {
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        List<Customer> customerList=new ArrayList<>();
        customerList.add(new Customer("ĐanTrường","22/04/2000","Đà Nẵng","data:image/jpeg;base64,"));
        customerList.add(new Customer("Quang Hải","15/01/2005","Huế","data:image/jpeg;base64,"));
        customerList.add(new Customer("Mai Hậu","08/05/2002","Sài Gòn","data:image/jpeg;base64,"));
        customerList.add(new Customer("Ngọc Trinh","14/08/2004","Gia Lai","data:image/jpeg;base64,"));
        request.setAttribute("customers",customerList);
        request.getRequestDispatcher("index.jsp").forward(request,response);
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {

    }
}
